import React, { useState } from "react";
import Card from "./components/Card";
import "bootstrap/dist/css/bootstrap.min.css";
import Settings from "./components/Settings";

function App() {

  const [cardColor, setCardColor] = useState("#ffffff");
  const [cardBorder, setCardBorder] = useState(false);
  const [cardSelect, setSelected] = useState();

  const films = [
    {
      id: 1,
      title: "Harry potter",
      category: "Fantasy",
      rating: 5,
      img: "data:image/jpeg;base64,"
    },
    {
      id: 2,
      title: "Friday 13",
      category: "Horror",
      rating: 4,
      img: "data:image/jpeg;base64,"
    },
    {
      id: 3,
      title: "Marvel",
      category: "Action",
      rating: 3,
      img: "data:image/jpeg;base64,"
    }
  ];

  const handleApply = (e) => {
    e.preventDefault();
  };


  return (
    <div className="container mt-5">

      <h2 className="mb-4">filmek</h2>

      <Settings handleApply={handleApply} setCardColor={setCardColor} setCardBorder={setCardBorder} setSelected={setSelected} />

      <div className="d-flex gap-3 flex-wrap">
        {films.map(film => (

          <Card
            key={film.id}
            title={film.title}
            category={film.category}
            rating={film.rating}
            img={film.img}
            cardColor={cardColor}
            cardborder={cardBorder}
            select={cardSelect}
          />

        ))}
      </div>
    </div>
  );
}

export default App;
